import java.nio.file.Path;
import java.util.Scanner;

/*
 * This Java source file was generated by the Gradle 'init' task.
 */
public class App {
    public static void main(String[] args) {

        System.out.println(getStringOfInputFilePath());
        System.out.println(getStringOfOutputFilePath());
        System.out.println(getTransformName());

    }

    /*************** Get User Input ***************/
    // Gets the input file path from the user and returns as a String
    public static String getStringOfInputFilePath() {
        String inputFilePathString;
        Scanner scanner = new Scanner(System.in);
        System.out.println("input file path: ");
        inputFilePathString = scanner.nextLine();
        return inputFilePathString;
    }

    // Gets the output file path from the user and returns as a String
    public static String getStringOfOutputFilePath() {
        String inputFilePathString;
        Scanner scanner = new Scanner(System.in);
        System.out.println("output file path: ");
        inputFilePathString = scanner.nextLine();
        return inputFilePathString;
    }

    // Gets the transform name from the user and returns as a String
    public static String getTransformName() {
        String outputFilePathString;
        Scanner scanner = new Scanner(System.in);
        System.out.println("transform name: ");
        outputFilePathString = scanner.nextLine();
        return outputFilePathString;
    }
}
